INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(14, '0～1岁宝宝感统合发展进程', '此阶段为口欲期，利用嘴唇寻找乳头，以获得食物与营养。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 01:59:33'),
(15, '1～2岁感觉统合发展进程', '喜欢以直接触碰的方式，增加对事物的认知。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:01:06'),
(16, '1岁的宝宝出现性别认同', '孩子的玩具会跟着出现性别分化，活动的偏好也会随之而来。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 08:43:25'),
(17, '1岁的宝宝开始产生同情心', '孩子会和同伴分享自己的玩具，甚至想帮父母做一些简单的家事。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 08:43:55'),
(18, '2～3岁的宝宝会自我分类', '他们对于人与人之间的不同变得更加敏锐，并且能依照这些面向将自己分类。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 08:44:23'),
(19, '2～3岁的宝宝会调整、隐藏和表达情绪', '孩子調整情緒的技巧有所進步，會將自己的注意力移開令他們感到沮喪的刺激。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 08:45:06'),
(20, '2～3岁感觉统合发展进程', '喜欢以实际的行动来满足好奇心、对家庭以外的生活空间产生好奇。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:04:45'),
(21, '2岁的宝宝出现衍生情绪', '出现自觉或自我评价的情绪，像是尴尬、害羞、内疚、骄傲等较复杂的情绪。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 08:56:57'),
(22, '2岁的宝宝有恐惧心理', '因为语言能力的发展，孩子2岁之后，开始产生恐惧心理。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:08:16'),
(23, '2岁的宝宝会出现有技巧的攻击行为', '在2到3岁时，幼儿会用肢体上的攻击，来报复让他们感到不舒服的玩伴。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:07:15'),
(24, '3～6岁的宝宝会积极表现自己的能力', '孩子会企图表现自己已经长大，有能力做很多事。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:23:06'),
(25, '4～5岁的宝宝有性别刻板印象', '性别刻板化的现象变得相当严格，玩具的偏好也变得更为强烈。', '', '', '心理', 'REC', '2014-10-14', '2014-10-15 06:09:06'),
(26, '4～5岁的宝宝开始理解别人的情绪', '幼儿已经能说明为什么别人会感到快乐、生气或哀伤。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:23:44'),
(27, '4～6岁开始帮助别人', '2到3岁会表现出同情和怜悯；4到6岁开始真正的助人行为。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:24:26'),
(28, '5岁开始发展他律性道德', '孩子会对规则非常的尊重，认为这些规则的内容是神圣不能更改的。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:24:53'),
(29, '18～24个月大的宝宝可从镜中的影像辨认自我', '孩子能认出照片中的自己，更会使用人称代名词「我」或自己的名字。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:26:03'),
(30, '24个月大的宝宝出现协调的互动关系', '知道自己有能力去引起某种东西或对方的响应，也可以协调自己的行动以达成目标。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:27:19'),
(31, '分离焦虑', '分离焦虑通常在婴儿6至8个月时出现，14至18个月时出现高峰。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:27:42'),
(32, '天生反射性动作', '宝宝会以哭泣、吸吮、抓握及踏步等多种生理上的反射动作，来表达自己的个性及气质。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:10:16'),
(33, '出现打人行为，不见得有攻击意图', '一岁左右的宝宝出现攻击行为时，并不代表具有攻击意图。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:30:04'),
(34, '出现陌生人焦虑', '8～10月的宝宝面对陌生人时，会出现「陌生人焦虑」。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:30:52');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(35, '出现喜怒哀乐的原始情绪', '宝宝在出生1个月时就有表达喜悦、生气、害怕等情绪的能力。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:31:16'),
(36, '如何培养婴幼儿之安全感与可信感', '依恋关系如果发展顺利，在二岁以后害怕分离的情况则会变得较不明显。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 02:44:55'),
(37, '何谓气质─宝宝的人格特质', '评估孩子的气质有九项标准，评估过后可将孩子的气质分为三种类型。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 09:46:56'),
(38, '判断并解决简单的问题', '18～24个月的宝宝，会开始动脑筋去解决简单的问题，以及察觉到物体的空间关系。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 09:31:46'),
(39, '依附型态', '婴儿和主要照顾者所建立的依附不仅有正向感情连结，也有较负向的型态存在。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:12:22'),
(40, '物体恒存的概念', '「物体恒存」是幼儿最早的抽象概念，也是孩子想象力及符号认知的基础。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 10:16:21'),
(41, '孩子的友谊', '宝宝可以从友谊表达中学会沟通的技巧，显露自己的内在需求，以及对于外在世界的感知。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 10:17:24'),
(42, '家有「缠」宝宝─谈孩子的分离焦虑', '1岁半到3岁间会出现分离焦虑，因为孩子认知能力开始明显，不想跟亲密的人分开。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:16:42'),
(43, '挫折忍受度', '幼儿阶段是孩子个性的确定期，即早展开挫折教育，将使他们能够坦然面对困难，从中学习成长。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 09:40:24'),
(44, '气质类型与教养方式', '决定宝宝气质是否改变的因素，就是他的气质类型与父母教养方式之间的「适配性」。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 10:40:40'),
(45, '特定依附期', '依附对象对宝宝来说是个安全堡垒，只要依附对象在宝宝视线范围，他就能安心到处探索。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 10:40:59'),
(46, '培养安全感', '婴幼儿期所发展的依恋关系，会影响到孩子将来的人际关系。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:21:33');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(47, '培养自信', '家长必须帮助孩子找出个人的特质，以增加孩子的自信，才能够让孩子发挥自己的专长。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 10:22:18'),
(48, '从声音辨识男女', '在6个月大时，婴儿就会利用说话声音的高低来区别男女。', '', '', '心理', 'REC', '2014-10-14', '2014-10-17 10:41:13'),
(49, '情绪本质不是情绪', '所谓的情绪本质与宝宝的内在情绪没有关系，它纯粹是宝宝外表给人的感觉。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:32:40'),
(50, '处理愤怒情绪', '宝宝受父母的影响很大，许多生活态度常是经由模仿父母的表现，再吸收扩大运用。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 09:27:25'),
(51, '发展友谊', '模仿是宝宝之间的沟通方式，并不是他们真的想要表达什么。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 09:27:44'),
(52, '当孩子遇到挫折时', '当孩子遇到挫折时，爸爸妈妈不必急着立刻帮他处理。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:35:38'),
(53, '影响依附类型的因素', '若照顾者能有耐心并依婴儿气质调整照顾方法，就能让婴儿与照顾者建立安全的依附关系。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 09:29:09'),
(54, '抚平孩子的伤口─单亲家庭', '该如何帮助孩子接受单亲家庭及往后的日子是家长所要注意的重点。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 09:30:02'),
(55, '适时与挫折为伍', '爸妈要能接纳宝宝的恐惧感，并且适时给予回应，而不是硬要他去面对那令他害怕的情境。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 09:31:25'),
(56, '学习事物运作的强烈动机', '宝宝可能会一再地将东西丢在地板上，他在记忆事物的因果关系。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 09:31:45'),
(57, '学习独立自主', '1到3岁这阶段，发展的危机是对自己的能力产生怀疑，在这阶段儿童必须学会「自主」。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 09:31:58'),
(58, '独生子女人际路难走', '在日常生活中因为缺乏竞争与吵闹的对象，并习惯于独处，可能就会产生两极化的结果。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:41:36'),
(59, '独生子女知多少', '父母要及时了解独生子女可能会产生的情况，从自己的管教方式做起。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 09:57:28'),
(60, '独生子女教养术', '多为独生子女安排和其它同侪相处的时间，让孩子练习与人相处。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:47:54');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(61, '独生宝宝交朋友教战守则', '幼儿是可塑性最大的阶段，亦是孩子发展良好人际关系的基础。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 10:30:45'),
(62, '辨识身体与自己的关系', '在出生后的2个月内，婴儿会重复以自己身体为中心的活动。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 06:06:11'),
(63, '选择渐进式的旋律', '宝宝在6个月开始就会倾听声音，爸妈可选择适合的音乐给宝宝听，促进他身心健康发育。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 10:29:38'),
(64, '婴幼儿、儿童气质', '从出生那天起，每个孩子就展现着不同的特性，这与生俱来的特性，我们称作「气质」。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 02:57:09'),
(65, '帮助孩子适应单亲的生活', '协助孩子适应单亲生活是家长将会面对的责任和义务。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 03:00:59'),
(66, '避免孩子得了童年富裕病', '丰富的物质虽可让孩子获得短暂快乐，但这种没有延迟的享乐却会是孩子日后生活的阻碍。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 10:35:49'),
(67, '宝宝也有大压力？', '如何与压力相处，并转化为正面力量，不但是成人自身、亦是孩子所必须面对的人生课题。', '', '', '心理', 'REC', '2014-10-14', '2014-10-23 03:02:50'),
(68, '宝宝的伙伴关系', '宝宝若是一直只有亲子间的对话，就无法正常拓展人际关系。', '', '', '心理', 'REC', '2014-10-14', '2014-10-20 04:30:02'),
(69, '0～1岁宝宝外出行前建议', '一个月后的宝宝，外出以住家附近为主，不建议远行。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-20 10:47:41'),
(70, '1～2岁促进发展小秘诀', '在婴幼儿的时期，爸妈要和宝宝做各种身体上的互动，刺激宝宝感觉的发展。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 02:54:33'),
(71, '1岁幼儿的建议食量', '当宝宝开始长牙，爸妈就能开始准备一些不一样的菜单。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 03:23:00'),
(72, '2～3岁幼儿的饮食', '两岁的孩子已经有良好的咀嚼能力，对食物也有强烈的意见。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 03:21:54'),
(73, '2～3岁促进发展小秘诀', '在安全的前提之下，宝宝可以在每个地方进行探索，家长也应尽量给予探索环境的机会。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 03:18:52'),
(74, '4～5岁幼儿的饮食', '四到五岁的幼儿对食物有强烈的偏好，而且转变的速度相当快。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 04:12:12');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(75, '9～10个月宝宝的游戏─照镜子', '爸妈可用手指着镜中，叫宝宝的名字，告诉他脸上表情和特色，帮助他认识自己的脸。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 04:14:14'),
(76, '一起来当超级奶爸', '爸爸参与育儿不但可以拉近亲子间的距离，对宝宝也会有很大的帮助。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-23 03:08:19'),
(77, '了解宝宝在学校的人际关系', '爸妈应关心宝宝在学校的交友情况，了解宝宝在行为发展过程中，可能遇到的疑难杂症。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 04:16:05'),
(78, '人际IQ180，从小培养起', '越早开启宝宝的人际IQ，对他将来的成长学习与人格发展有很大的帮助。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-22 10:52:27'),
(79, '上下铺的床', '上铺的床垫要和床契合，上铺的护栏每支间距不可超过十公分。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 05:43:04'),
(80, '口腔探索扩展刺激', '宝宝会不断地将每样东西都放进自己的口中，藉以认识并学习这些既陌生又有趣的物品。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:10:48'),
(81, '小宝宝是电灯泡', '当个主动出击者，好好观察宝宝的睡眠时间避免宝宝的打扰。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 07:57:51'),
(82, '不要和别人比较', '宝宝刚好是在整个区间较尾端的部份，爸爸妈妈可以协助宝宝发展出该能力。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:13:11'),
(83, '手足和发展间的关系', '兄弟姐妹间的相处往往有助于培养许多其它能力。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:13:38'),
(84, '手眼协调能力', '若是宝宝的大脑及手部没有经常练习，就无法拥有良好的平衡感及手眼协调的能力。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:14:00'),
(85, '父母易犯的错误', '父母犯的这些错误往往会造成管教无效或是亲子间的争执、隔阂。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:17:26'),
(86, '父母的分离焦虑', '爸爸妈妈和宝宝难分难舍，会造成严重的分离焦虑。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:18:28'),
(87, '奶瓶一定要消毒', '宝宝6个月大以前，冲调奶粉用的奶瓶及器具都应做好确实消毒。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:20:38'),
(88, '奶瓶龋齿，不当吸吮是祸首', '18个月大的宝宝这时期的蛀牙，会间接或直接影响到宝宝以后牙齿的生长发育。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-23 03:10:39'),
(89, '幼儿自行沐浴的安全', '在浴室中加装各种安全措施，教导小孩正确使用水龙头的方式。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:23:17'),
(90, '打造婴儿房要点', '婴儿房内的地板材质，最好选择木质地板；房间的温度控制在摄氏25～26度左右。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:25:30'),
(91, '未满3岁的孩子解决手足间的冲突', '有关化解冲突的正向技能，都能在手足争执中学习到。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:27:46'),
(92, '用双手探索事物', '六个月大的宝宝已经能够有目的性地用自己的双手，促进他认知的发展。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:28:42');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(93, '白天托婴会影响宝宝脑力发展吗？', '家长可以建议保姆在白天给宝宝适当的刺激，像是放音乐给宝宝听。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:29:28'),
(94, '交谈促进语言发展', '七个月大的宝宝已经能够听懂特定词语，例如他的名字以及一些对他有意义的物品名称。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:30:02'),
(95, '各角落的安全', '很多孩子的意外伤害都是发生在家里，因此家中的安全显得格外重要。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:32:31'),
(96, '回答孩子的问题', '宝宝从三岁开始，会对这个世界充满好奇心，可能会缠着你不停的问「为什么」。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:33:06'),
(97, '多鼓励孩子', '鼓励孩子行为的进步，他会感觉到所做的行为是好的，所以会再去实践被鼓励的行为。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-23 03:13:30'),
(98, '如何替宝宝挑双好鞋', '当宝宝站立时，脚掌的形状常会因本身体重的压迫而变形。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:50:54'),
(99, '如何解读宝宝的行为', '宝宝是很好奇的，有许多动作都是出自于模仿或本能反应。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-22 10:41:38'),
(100, '如何帮助宝宝脱离安抚奶嘴', '过度使用安抚奶嘴，会导致宝宝牙齿变形、咬合不正，将来还可能出现戒除困难的问题。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 06:58:00'),
(101, '如何避免养出胖宝宝？', '你的宝宝胖吗？坚守「严禁甜食、多吃蔬果」的原则，养成散步习惯，就不怕养出胖宝宝。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-22 10:36:06'),
(104, '如何让孩子不赖床', '爸爸妈妈要让孩子养成规律的作息习惯，在固定时间上床睡觉。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-23 03:14:53'),
(105, '忙碌时的情绪管理', '了解自己、宝宝和家人的作息，以及周遭能协助支持的作息。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 07:28:23'),
(106, '有学龄前幼儿的家庭发展任务', '早期教育得当能为孩子建立良好的基础，夫妻双方的教育方式与观念也很重要。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 07:28:41'),
(107, '有婴儿的家庭发展任务', '当第一个孩子诞生，就为家庭生命周期中的「婴儿期」揭开序幕。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 07:28:57'),
(108, '老大别吃醋', '爸爸妈妈在提供孩子玩具、衣服、食物等资源时，要先考虑能不能让孩子和平地共享。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-22 10:27:30');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(109, '快快乐乐出门', '带宝宝出外旅行除了所携带的行李外，还会带个随身背包装一些小对象。', '', '', '育儿', 'REC', '2014-10-15', '2014-10-21 07:54:55'),
(110, '我家宝宝爱吃饭', '满一岁到一岁三个月之间，是诱导宝宝自己吃饭的黄金期。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 08:07:45'),
(111, '把东西往嘴里塞，就是长牙了', '宝宝约在6个月大时，牙齿便从牙龈上冒出来。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 10:24:03'),
(112, '把孩子给祖父母照顾好吗？', '爸妈决定把孩子交给祖父母照顾之前，双方一定要做好沟通，了解祖父母是否愿意帮忙。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 10:21:39'),
(113, '汽车安全座椅', '购买汽车安全座椅时，家长最好能带孩童一起同行，让他们试坐各种款式的座椅。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 08:22:59'),
(114, '和其它新手父母共享经验', '安排一些聚会，与新手父母们交换育婴心得，可以减轻一些压力及不确定的感觉。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 10:15:30'),
(115, '和宝宝聊聊天', '六个月大的婴儿能够听懂一些声调和元音，对婴儿说话可以提升婴儿学习新字汇的速度。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 10:13:21'),
(116, '爬爬走发展视觉空间智能', '在宝宝摸索学习爬行的过程当中，他对空间的概念会更加明确。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 08:24:21'),
(117, '迎接新生儿要准备什么', '育婴必备用品分为婴儿衣物、寝具、喂食器具、尿片用具、沐浴用具、外出用品这六大项。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-23 03:18:31'),
(118, '保母的选择', '帮宝宝选择优良的保母，可以使爸妈安心工作，也能减缓爸妈与宝宝分离的情绪压力。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 10:09:43'),
(119, '娃娃车安全', '托儿所交通车车龄不得超过10年，车内需备有幼童专用座椅。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 10:07:07'),
(120, '孩子哭闹时父母的情绪管理', '爸妈遇到宝宝哭闹时，不要一味禁止或大声斥责他，应先用同理心关怀宝宝内在的情绪。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:07:48'),
(121, '孩子对父母离婚的反应', '学龄前期的小朋友对父母离婚会出现较激烈的情绪，并出现严重的分离焦虑和攻击行为。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-23 03:20:10'),
(122, '孩子说谎怎么办？', '小谎在宝宝身上似乎常常发生，不适时予以纠正只怕长大后扯的谎就不只如此了。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:13:54');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(123, '挑选奶粉要注意什么？', '在喂食宝宝之前，首先要了解婴幼儿营养需求特点。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:14:50'),
(124, '为人父母的调适过程', '趁宝宝入睡时，或将宝宝交给信任的人照顾一小段时间，和另一半好好的放松一下。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:16:31'),
(125, '为什么孩子总是不听话、调皮捣蛋？', '爸妈不要一味的用责备的方式来管教他们，应该从行为之后的后果来探究他们的动机。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:17:32'),
(126, '音乐铃、手摇铃发展宝宝听觉', '倾听各种声音能让宝宝的脑神经成长，对声音有反应也能促使运动神经发展。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:17:08'),
(127, '家庭时间如何分配', '您可以依照事情的先后缓及每天做不同的设定，但家庭时间的需求是每天固定要有的。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:19:51'),
(128, '捉迷藏游戏', '宝宝看不见你时，他会开始臆测人或物品其实是一直存在的，渐渐发展出物体恒存的概念。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:20:18'),
(129, '特殊宝宝父母的心理调适', '爸爸妈妈应调整心情及作息，为宝宝规划一个完善的未来。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:20:59'),
(130, '祖父母教养孙儿的方式', '祖父母应与子女讨论，建立一套有原则且一致的教养方法，并与孙儿共同遵守此原则。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:21:52'),
(131, '祖父母该如何面对隔代教养', '建议爷爷奶奶在带孩子之前，应该先衡量自身的能力及状况，是否真的适合照顾孙儿。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:22:52'),
(132, '讨厌的尘蹒', '室内的螨虫是过敏症患者的主要致病原因之一。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:24:28'),
(133, '训练宝宝上厕所', '让宝宝早点自己上厕所，不只可省去处理小朋友秽物的时间，连花在尿布的费用都能省下。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 08:50:49'),
(134, '训练宝宝自己穿戴衣物', '宝宝扣钮扣、按钮扣、拉拉炼、系鞋带、绑蝴蝶结等的技巧，可以训练其动作能力的成熟度。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 08:43:16'),
(135, '训练宝宝的肢体动作', '爬行可训练孩子的协调能力，使宝宝在学会走路后不容易跌倒。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 08:39:32'),
(136, '做好孩子的榜样', '五岁可以说是孩子社会发展重要的时期，这时候他所看到、听到的都会影响他的人格。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 09:57:19'),
(137, '做劳作真好玩', '在制作纸娃娃的过程中，可让宝宝练习分类的能力，并且训练他的精细动作。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 08:37:46'),
(138, '动手操作真好玩', '1岁左右的宝宝对于使东西发出声音以及物体的功能感到非常有兴趣。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:10:14');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(139, '念书给宝宝听', '念故事书给5、6个月大的宝宝听，可以训练他的语文能力。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 08:36:01'),
(140, '细心呵护小乳牙，宝宝不再无「齿」', '进食后和睡前清洁牙齿最为重要，要把附着在牙齿和牙龈上的食物残渣和牙菌斑清除干净。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 08:33:11'),
(141, '责备的艺术', '正确的责备应该是明确而严肃的为宝宝指出「对的事」和「不对的事」这之间的区别。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:16:41'),
(142, '透过观察学习', '5个月大的宝宝绝大多数是透过观察来学习，因此要尽量给他观看的机会。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:17:16'),
(143, '陪宝宝说故事', '对于3岁以下宝宝来说，即使说的是同一个故事，他们也是百听不厌。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:17:49'),
(144, '替宝宝打嗝', '宝宝在喝完奶之后都必须替他打嗝，因为宝宝的消化系统不会自动地为自己做这件事。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:19:11'),
(146, '爱运动的宝宝不生病', '爸妈应培养孩子对运动的兴趣，让宝宝从简单的跑跳活动开始，慢慢发展至运动性的游戏。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:19:53'),
(147, '搔搔痒发展宝宝触觉', '对婴儿期的宝宝而言，触觉刺激是很重要的。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:20:59'),
(148, '新生儿衣服仔细挑', '干净利落的剪裁，尽量不要在大腿、手臂与颈部有束边和纤线外露。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-23 03:27:37'),
(149, '新生儿的视觉发展', '刚出生的宝宝，看见的东西是模糊不清的，出生后他的双眼焦距渐渐形成。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:26:18'),
(150, '新生儿的头发', '宝宝的头发需用软毛刷或圆头齿梳每天梳理。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:26:46'),
(151, '新生儿的触觉发展', '宝宝特别需要被抚触，若是没有，他就会在身体与精神上显示出强烈的退缩与挫折感。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:27:35'),
(153, '新生儿的听觉发展', '宝宝出生不久，就开始主动运用他与生俱来的一些基本行为模式对环境中的事物做出反应。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:28:40'),
(154, '新生儿感统促进发展小秘诀', '在新生儿的时期，爸爸妈妈就要和宝宝做各种身体上的互动，刺激宝宝感觉的发展。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:42:37'),
(155, '照顾宝宝的肚脐', '包脐带的纱布要保持清洁，如果湿了要及时换干净。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-21 10:43:52'),
(156, '当孩子和你生气时', '先了解孩子生气的原因，然后再好好和他沟通及告知，提醒他这行为是不对的。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 08:12:34'),
(157, '隔代教养的父母该做些什么', '父母应与孩子及祖父母多多联系，了解孩子的状况。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-16 10:45:58');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(158, '零用钱', '建立孩子金钱观可以从日常生活做起，利用带宝宝上超市时，灌输金钱要妥善运用的观念。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 01:57:26'),
(159, '对自身以外事物感到兴趣', '4个月大的宝宝已能看清楚眼前的影像，无论是父母，或窗外的小鸟，都可吸引他的注意力。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 01:57:56'),
(161, '对语言的回应', '宝宝从七个月开始，当爸爸妈妈等特定的人说话时，他会有反应。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 02:05:21'),
(162, '认识各种婴幼儿奶粉', '当宝宝四个月大后，母乳的营养将渐渐不敷生长所需，添加副食品是此时最重要的课题。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 08:03:32'),
(163, '语言发声能力', '宝宝在六个月左右，会发出一些比刚出生时更明确的元音。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 03:28:56'),
(164, '撞伤和意外坠落', '家中若是有正在学习爬行的婴幼儿，家长应特别注意家具的摆设安全。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-23 03:31:16'),
(165, '抚摸身体', '缺乏抚触拥抱的宝宝，较容易产生暴躁的情绪。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-23 03:31:41'),
(166, '模仿动物游戏', '藉由此活动可以训练宝宝的发音及肢体发展，还可以让宝宝认识动物。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 07:57:28'),
(167, '适合4～5个月宝宝的运动', '这时家长可以协助宝宝作起身的训练，让宝宝手握着大人的手指，「慢慢地」将宝宝拉起。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-23 03:32:20'),
(168, '适应爸爸这个角色', '和妻子聊聊关于当父母所产生的问题或交换心得，有人了解和分享，心情就能很快好起来。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 03:37:29'),
(169, '鞋鞋游戏好好玩', '把穿鞋子当成游戏来与宝宝同乐，能让宝宝在愉快中早日学会，更能增进亲子感情。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-23 03:33:25'),
(170, '学步期宝宝的床', '确保坚固与安全性，勿离地面过高，床铺设计不过于繁复，并将床铺安置在安全的角落。', '', '', '育儿', 'REC', '2014-10-16', '2014-10-22 03:46:21'),
(171, '亲子共读', '家长若能在孩子成长过程及早提供文字丰富的环境，对于幼儿的语文发展将有很大的帮助。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-23 03:34:15'),
(172, '挑一个优良的托儿所环境', '爸妈应和托儿所内的老师谈谈他们的课程设计、活动及教学方式，及询问教学目标及理念。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-23 09:59:42'),
(173, '婴幼儿口腔清洁', '3岁左右应让孩子养成早晚刷牙、饭后漱口的习惯。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 04:08:35'),
(174, '婴幼儿的皮肤保健', '婴幼儿腹泻、不常换尿布，加上尿布对皮肤的摩擦。危险因素越多，越容易产生尿布疹。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 04:25:57');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(175, '婴儿房必备物品', '寝具要舒适、卫生，床垫要平，不要太软，可采用移动方便的活动床。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 04:28:38'),
(176, '婴儿房的注意事项', '婴儿房最好紧邻父母房，如此宝宝有什么状况才能立刻察觉，晚上要照顾时也比较方便。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 04:30:24'),
(177, '帮宝贝剪指甲', '修剪的时间最好是宝宝刚洗完澡的时候，因为此时指甲会发软，容易剪掉。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-23 03:36:22'),
(178, '喂奶后小心食道逆流', '食道逆流，为幼儿常见的疾病，大多是在宝宝出生后不久后即会发生的症状。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 07:29:59'),
(179, '喂宝宝吃副食品', '宝宝在四个月～六个月时已有咀嚼能力，可开始添加副食品。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 05:44:27'),
(180, '断奶的工作', '断奶是循序渐进的，不是突然中断或是在4个月前或6个月后才开始。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 05:45:11'),
(181, '双生涯家庭的适应', '在两个人都有工作的情况下，夫妻双方要能相互感受、表达和分享。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 05:46:01'),
(182, '宝宝可以自己睡吗？', '有些家庭采取让宝宝睡在婴儿床，而与大人同房的做法，这样的方式较为正确。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 05:46:16'),
(183, '宝宝如厕动作发展', '当孩子一岁半开始，父母便可以根据孩子的状况，开始训练他如厕的习惯。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 07:23:03'),
(184, '宝宝尿床怎么办', '尿床通常不是因为睡前喝太多水，也不是发展或智力的问题。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 06:02:29'),
(185, '宝宝怕生、害羞吗？', '趋进性高的宝宝通常可以很快接受第一次接触到的新事物。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-23 03:40:10'),
(186, '宝宝性别发展第一步─从声音辨识男女', '在6个月大时，婴儿就会利用说话声音的高低来区别男女。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 06:06:23'),
(187, '宝宝爬行好处多', '宝宝学会爬行将扩大视野和接触范围，对于大脑的发育和智力的开发具有非常重要的意义。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 07:02:01'),
(188, '宝宝的想象力', '说故事也可以展现宝宝的想象力，他往往对着你讲故事，一讲就停不下。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 06:12:14'),
(189, '宝宝洗香香', '在替宝宝沐浴前，先将必要的用品准备好，以愉悦的心情跟孩子享受沐浴的乐趣。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-23 03:42:57');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(190, '宝宝看电视', '尽量抽空和孩子一起看电视是必需的，父母应该了解宝宝最喜欢的节目或卡通是什么内容？', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 07:05:22'),
(191, '宝宝与宠物', '爸妈对宠物付出的爱心和教育，会让孩子了解该如何照顾宠物及人类和动物之间怎么相处。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 06:39:08'),
(192, '宝宝撞断乳牙该如何处理？', '在恒齿尚未长出来之前，撞断乳牙的宝宝还不须制作假牙。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-24 06:35:09'),
(193, '让2～3岁的孩子画画', '画画可以启发孩子的智能，培养孩子的审美情趣。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 07:06:20'),
(194, '让孩子为自己的行为负责', '不强迫孩子顺从，鼓励他们做负责任的决定，不论这决定是好或坏，孩子都要自己负责。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 07:07:41'),
(195, '让宝宝记住个人资讯', '个人资讯对宝宝来说非常的重要，而简短的资讯对宝宝来说并不会太难记住。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-22 07:08:50'),
(196, '让宝宝聪明懂礼貌', '爸妈可运用生活中的各种情境，随时随地把握学习的机会，教导宝宝了解「礼貌」的观念。', '', '', '育儿', 'REC', '2014-10-17', '2014-10-23 03:48:20'),
(197, '0～1个月宝宝的认知发展', '婴儿出生后，动作相当局限于使用天生的反射动作，都以先天的无条件反射动作适应环境。', '', '', '教育', 'REC', '2014-10-17', '2014-10-23 03:49:27'),
(198, '0～2个月宝宝适合的玩具', '新生儿对声音较有反应，因此给予丰富的感官刺激是此时重要的任务。', '', '', '教育', 'REC', '2014-10-17', '2014-10-23 03:52:18'),
(199, '0岁教具制作DIY', '0岁宝宝的发展任务主要是建立对人及周遭世界的信任、建立与照顾者间的亲密关系。', '', '', '教育', 'REC', '2014-10-17', '2014-10-23 03:57:46'),
(200, '1～4个月宝宝的认知发展', '宝宝在一到四个月大时，还不会去寻找隐藏于视野之外的物体。', '', '', '教育', 'REC', '2014-10-17', '2014-10-23 03:58:13'),
(201, '1岁半～2岁宝宝的游戏─模仿动物', '藉由此活动可以训练宝宝的发音及肢体发展，还可以认识动物，并且让宝宝集中注意力喔！', '', '', '教育', 'REC', '2014-10-17', '2014-10-23 03:59:51'),
(202, '1岁宝宝开始记忆单字', '１岁的宝宝会从日常生活与身边环境中开始学习语言。', '', '', '教育', 'REC', '2014-10-17', '2014-10-23 04:00:33'),
(203, '2～3岁教具制作DIY', '应提供2～3岁宝宝练习精细动作的机会，不仅能训练小肌肉，也可练习手眼协调。', '', '', '教育', 'REC', '2014-10-17', '2014-10-23 04:06:26'),
(204, '2～3岁宝宝适合的玩具', '善用此时宝宝正值观察力和模仿力强的敏锐期，培养宝宝内在主动的独立性格。', '', '', '教育', 'REC', '2014-10-17', '2014-10-23 04:13:26');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(208, '3～4岁宝宝适合的玩具', '3～4岁的孩子开始具有时间的概念，可以预期每天固定会发生的例行事务。', '', '', '教育', 'REC', '2014-10-17', '2014-10-23 04:21:36'),
(214, '3～4岁教具制作DIY', '利用文字搭配图形，能增进宝宝的认知能力，并强化宝宝配对的能力', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 04:35:00'),
(215, '3～4岁宝宝适合的玩具', '3～4岁的孩子开始具有时间的概念，可以预期每天固定会发生的例行事务', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 05:44:04'),
(216, '4～5岁教具制作DIY', '在宝宝已有相当的生活经验后，便可提供小游戏以刺激听觉发展', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 05:48:59'),
(217, '4～5岁宝宝适合的游戏', '四岁的孩子会透过无止尽的问题、探索和个人经验，特别是透过游戏来学习', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 05:55:06'),
(218, '4～8个月宝宝的认知发展', '从4个月开始，宝宝在视觉与抓握动作之间形成了协调，开始用手触摸、摆弄周围的物体', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 06:02:45'),
(219, '5～6个月宝宝适合的玩具', '这个阶段，手眼协调动作发生了，宝宝可以准确地把手伸向玩具', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 06:07:59'),
(221, '5～6岁教具制作DIY', '4岁后的幼儿活动力渐渐提升，此游戏可让孩子在家就达到运动的功能并促进认知的发展', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 06:40:27'),
(222, '5～6岁宝宝适合的玩具', '5岁后的孩子语言能力突飞猛进，可以藉由发问、交谈与观察来学习', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 06:48:31'),
(223, '7～8个月宝宝可训练的能力', ' 7个月左右的宝宝已渐渐能听懂父母的话，所以家长可以开始教导孩子一些基本的观念', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 06:50:47'),
(224, '7～9个月宝宝适合的玩具', '适度的给宝宝几个玩具，启发宝宝想出各种玩的方法，这样的效果会比提供很多玩具更好', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 06:54:56'),
(225, '8～12个月宝宝的认知发展', '这时运用各动作模式之间的配合更加灵活，并能运用不同的动作模式来应付遇到的新事物', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 06:56:55'),
(226, '10～12个月宝宝适合的玩具', '宝宝开始爬行进而学会行走，活动力大增，这时候的玩具尽量以能和宝宝互动的为主', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 07:09:38'),
(227, '12～18个月宝宝适合的玩具', '单纯的摇铃或是只会发出声音的玩具已不再吸引他们，他们的思维技能已有初步的进展', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 07:32:56');
INSERT INTO `articles` (`uid`, `title`, `description`, `content`, `image`, `tag`, `type`, `pub_date`, `last_updated`) VALUES
(228, '12～24个月教具制作DIY', '宝宝已开始利用四肢探索世界，父母可提供刺激大肌肉发展的游戏，让他练习以促进发展', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 07:40:05'),
(229, '18～24个月宝宝的游戏─来数数', '这游戏能让宝宝认识1～5的数字，以及这些数字所配合的数量，并加强他们唱数的能力', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 07:41:56'),
(230, '18～24个月宝宝的认知发展', '一岁半到两岁的宝宝，除了用身体和外部动作寻找新方法，还能开始动脑「想出」新方法', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 07:43:34'),
(231, '18～24个月宝宝适合的玩具', '宝宝开始会动脑筋解决事情，像是在玩拼图的时候，会根据边缘形状及洞孔去契合', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 07:48:53'),
(232, '一岁半宝宝可以作的教养训练', '一岁半左右的小孩已经开始可以了解并完成简单的指令', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 07:54:50'),
(233, '三岁宝宝的语言学习', '三岁孩子的字汇能力正不断地扩充，多让他发表自己的意见', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 07:59:25'),
(234, '上小学前的准备', '慢慢与孩子沟通，透过讨论、分享，多提供机会让孩子自己做决定，学习处理问题', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 08:01:08'),
(235, '不同教养方法对子女的影响', '管教方式对子女会有不同影响，除了影响到子女的性格发展，最直接的影响就是亲子关系', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 08:03:22'),
(236, '六大资优小孩类型', '资优定义为在学前、小学或中学阶段的儿童在不同方面表现杰出者都算是资优生', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 08:30:25'),
(237, '比手画脚可以跟宝宝沟通吗？', '宝宝本来就会运用一些简单的肢体语言来表达需求', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 08:36:31'),
(238, '四岁宝宝的语言学习', '四岁的宝宝可能已经认得几个特定的字汇，尤其是他姓名中的字', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 08:38:41'),
(239, '幼儿音乐的选择与欣赏方式', '建议爸爸妈妈每天可找出一小段时间与宝宝共同分享音乐', '', '', '教育', 'REC', '2014-10-23', '2014-10-23 08:42:17'),
